import React from "react";
import { useAuth0 } from "@auth0/auth0-react";
import ChatBot from "./ChatBot";

function App() {
  const { loginWithRedirect, logout, isAuthenticated, getAccessTokenSilently, user, isLoading } = useAuth0();

  if (isLoading) return <div>Loading...</div>;

  return (
    <div style={{ padding: "20px" }}>
      {!isAuthenticated ? (
        <button onClick={() => loginWithRedirect()}>Login</button>
      ) : (
        <>
          <div>
            Logged in as {user.name} <button onClick={() => logout({ returnTo: window.location.origin })}>Logout</button>
          </div>
          <ChatBot getToken={getAccessTokenSilently} />
        </>
      )}
    </div>
  );
}

export default App;
