import { useState } from "react";

export default function ChatBot({ getToken }) {
  const [query, setQuery] = useState("");
  const [result, setResult] = useState("");

  const handleSend = async () => {
    if (!query) return;

    try {
      // Get the Auth0 access token for the user
      const token = await getToken();

      // Call your FastAPI backend
      const response = await fetch("http://127.0.0.1:8000/analyze", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          "x-token": token
        },
        body: JSON.stringify({ query })
      });

      const data = await response.json();
      setResult(data.explanation || JSON.stringify(data));
    } catch (error) {
      console.error("Error calling SOC bot:", error);
      setResult("Error calling SOC bot. Check console for details.");
    }
  };

  return (
    <div>
      <h2>SOC Bot Chat</h2>
      <input
        type="text"
        value={query}
        onChange={(e) => setQuery(e.target.value)}
        placeholder="Enter IP, malware, or CVE..."
        style={{ width: "300px" }}
      />
      <button onClick={handleSend} style={{ marginLeft: "10px" }}>
        Ask
      </button>

      {result && (
        <div style={{ marginTop: "20px", whiteSpace: "pre-line" }}>
          <strong>Result:</strong>
          <p>{result}</p>
        </div>
      )}
    </div>
  );
}
