import React, { useState } from "react";

function ChatBot({ getToken }) {
  const [messages, setMessages] = useState([
    { sender: "bot", text: "Hello! I am your SOC Assistant. Ask me about IPs, malware, CVEs, etc." }
  ]);
  const [input, setInput] = useState("");

  const BACKEND_URL = process.env.REACT_APP_BACKEND_URL || "http://127.0.0.1:8000";

  const handleSend = async () => {
    if (!input.trim()) return;

    setMessages(prev => [...prev, { sender: "user", text: input }]);

    try {
      const token = await getToken();
      if (!token) throw new Error("JWT token not found");

      const response = await fetch(`${BACKEND_URL}/analyze`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          "x-token": token
        },
        body: JSON.stringify({ query: input })
      });

      if (!response.ok) throw new Error(`Backend error: ${response.status}`);

      const data = await response.json();
      setMessages(prev => [...prev, { sender: "bot", text: data.result }]);
    } catch (err) {
      console.error("Error calling backend:", err);
      setMessages(prev => [...prev, { sender: "bot", text: "Error connecting to backend" }]);
    }

    setInput("");
  };

  const handleKeyPress = e => {
    if (e.key === "Enter") handleSend();
  };

  return (
    <div style={{ border: "1px solid #ccc", padding: "15px", maxWidth: "600px" }}>
      <div style={{ height: "300px", overflowY: "auto", marginBottom: "10px" }}>
        {messages.map((msg, idx) => (
          <div key={idx} style={{ margin: "5px 0", textAlign: msg.sender === "user" ? "right" : "left" }}>
            <span
              style={{
                display: "inline-block",
                padding: "8px 12px",
                borderRadius: "12px",
                backgroundColor: msg.sender === "user" ? "#DCF8C6" : "#F1F0F0"
              }}
            >
              {msg.text}
            </span>
          </div>
        ))}
      </div>

      <input
        type="text"
        value={input}
        onChange={e => setInput(e.target.value)}
        onKeyPress={handleKeyPress}
        placeholder="Type your query..."
        style={{ width: "80%", padding: "8px", fontSize: "16px" }}
      />
      <button onClick={handleSend} style={{ padding: "8px 16px", marginLeft: "10px" }}>
        Send
      </button>
    </div>
  );
}

export default ChatBot;
